import { render } from '@testing-library/react';
import React, { Component } from 'react';
import './App.css';


class App extends Component{


  state = {
    person: {
      fullName: "hager",
       bio: "nabil",
       imgSrc: "image",
       profession: "banker" ,
       }
      }

    change = () => {
      this.setState({
        age: 30,
      })(true)
    }


  render() {
    return (
      <div className="App">
        <button onClick={this.change}>show profile</button>
        <p>{this.state.bio}</p>
      </div>
    );
  }
}
