import { render } from '@testing-library/react';
import { Component } from 'react';
import './App.css';
import React from 'react'

class App extends Component{


  state = {
    person: {
      fullName: "hager",
       bio: "nabil",
       imgSrc: "image",
       profession: "banker" ,
       }
      }

    change = () => {
      this.setState({
        age: 30,
      })(true)
    }


  render(){
    return <div>
     {this.state.person.fullName} {this.state.person.bio} {this.state.person.imgSrc} {this.state.person.profession}
     <button onClick={this.change.age}>
       show
     </button>
    </div>
  }
}



export default App;
